var button=document.querySelector(".search-button"),popup=document.querySelector(".hotel-search"),arrival=popup.querySelector("[name=arrival]"),departure=popup.querySelector("[name=departure]"),form=popup.querySelector("form");button.addEventListener("click",function(e){e.preventDefault(),popup.classList.toggle("hotel-search-show"),arrival.focus()}),form.addEventListener("submit",function(e){arrival.value&&departure.value||(e.preventDefault(),popup.classList.remove("hotel-search-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("hotel-search-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("hotel-search-show")&&(e.preventDefault(),popup.classList.remove("hotel-search-show"),popup.classList.remove("hotel-search-error"))});